def getFirst(values):
	if values is None or len(values) == 0:
		return None
	return values[0]

def invoke(msg):
	events = 0
	alerts = 0
	messages = 0
	commands = 0
	values = msg.get("user")
	for row in values:
		key = row.get('data_key')
		if "events" == key:
			events = row.get('data_value')
		elif "alerts" == key:
			alerts = row.get('data_value')
		elif "messages" == key:
			messages = row.get('data_value')
		elif "commands" == key:
			commands = row.get('data_value')
	msg.put("acct.events", events)
	msg.put("acct.alerts", alerts)
	msg.put("acct.messages", messages)
	msg.put("acct.commands", commands)
	return True

