def invoke(msg): 
	idsToSend = ''
	ids = msg.get("devices.reg_id")
	if ids == None:
		return False
	if len(ids) == 1:
		idsToSend = '"%s"' % ids[0]
	else:
		for id in ids:
			idsToSend = '%s, "%s"' % (idsToSend, id)
	msg.put("regIdsToSend", idsToSend)
	return True

