from com.vordel.trace import Trace

def invoke(msg):
	s = ''
	ids = []
	triggers = msg.get("triggers")
	if triggers is None or len(triggers) == 0:
		pass
	else:
	 	for t in triggers:
			id = t.get("acct_id")
			if id in ids:
				continue
			ids.append(id)
	if len(ids) == 0:
		return False
	msg.put("trigger_acct_ids", ','.join(ids))
	Trace.debug('trigger_acct_ids: %s' % ','.join(ids))
	return True

