from org.json.simple import JSONArray, JSONObject, JSONValue from com.vordel.trace import Trace
def toJson(t): rv = JSONObject() if t is None: return None; rv.put("id", t.get("id")) rv.put("status", t.get("status")) rv.put("flag", t.get("flag")) rv.put("trigger_type", t.get("trigger_type")) rv.put("name", t.get("name")) rv.put("expression", t.get("expression")) rv.put("priority", t.get("priority")) return rv;
def invoke(msg): triggers = msg.get("db.triggers") jsonArr = JSONArray() n = triggers.size() Trace.debug('triggers size: %d' % n) for i in range(len(triggers)): t = triggers[i] j = toJson(t) if j is not None: s = JSONValue.toJSONString(j) Trace.debug('adding %s' % s) jsonArr.add(j) s = JSONValue.toJSONString(jsonArr) Trace.debug(('returning %s' % s)) msg.put("output.json", s) return True

